INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4496, 11849, 'Lorlatinib', 'Hyperglycemia', 'The use of lorlatinib might cause hyperglycemia.  It is recommended to assess and control fasting serum glucose prior to initiation of treatment and monitor periodically thereafter.  Caution should be exercised when treating diabetic patients.  Withhold and resume at a reduced dose or permanently discontinue treatment based on severity.', '2', '', 'DDInter', 0);
