INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4512, 22082, 'Loxapine', 'Epilepsy', 'Antipsychotics can lower the seizure threshold and trigger seizures in a dose-dependent manner.  Seizures have been reported in patients receiving antipsychotic therapy and may occur in epileptic patients even with maintenance of routine anticonvulsant treatment.  Therapy with antipsychotics should be administered cautiously in patients with a history of seizures or other predisposing factors, such as head trauma, CNS abnormalities, and alcoholism.  High dosages should be avoided if possible.', '3', '', 'DDInter', 0);
