INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (4534, 22082, 'Loxapine', 'Liver Diseases', 'Most neuroleptic agents are extensively metabolized by the liver.  The plasma concentrations of these agents may be increased and the half-lives prolonged in patients with impaired hepatic function.  Therapy with neuroleptic agents should be administered cautiously in patients with significant liver disease.  Lower initial dosages and slower titration may be appropriate.', '2', '', 'DDInter', 0);
