INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (5048, 3797, 'Magnesium sulfate', 'Arrhythmias, Cardiac', 'The use of osmotic laxatives preparations, containing sodium sulfate, potassium sulfate, and magnesium sulfate may cause cardiac arrhythmias.  There have been rare reports of serious arrhythmias associated with the use of ionic osmotic laxative products for bowel preparation.  Patients with electrolyte abnormalities should have them corrected before treatment is initiated.  Use caution when prescribing preparations in patients at increased risk of arrhythmias or with a history of seizures.  It is recommended to conduct a pre-dose and post-colonoscopy ECGs in patients at increased risk of serious cardiac arrhythmias.', '2', '', 'DDInter', 0);
