INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (5123, 3420, 'Medroxyprogesterone acetate', 'Bone Diseases, Metabolic', 'The use of intramuscular medroxyprogesterone for contraception has been shown to induce bone loss, particularly during the early years of therapy.  With continued use, the decline in bone density subsequently approaches the normal rate of age-related loss.  Therapy with parenteral medroxyprogesterone should be administered cautiously in patients with osteoporosis or chronic use of drugs that can reduce bone mass, such as anticonvulsants or corticosteroids.', '3', '', 'DDInter', 0);
