INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (5265, 7659, 'Mefenamic acid', 'Hyperkalemia', 'Increases in serum potassium concentration (including hyperkalemia) have been reported with use of nonsteroidal anti-inflammatory drugs (NSAIDs), even in some patients without renal impairment.  In patients with normal renal function, these effects have been attributed to a hyporeninemic-hypoaldosteronism state.  Caution is advised in patients with hyperkalemia.', '2', '', 'DDInter', 0);
