INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (5815, 1611, 'Meropenem', 'Central Nervous System Diseases', 'The intravenous use of carbapenems has been associated with central nervous system adverse effects such as seizures (up to 1.5%) and, less frequently, somnolence, encephalopathy, myoclonus, tremor, paresthesia, confusion, agitation, depression, and hallucinations.  Therapy with carbapenems, regardless of route of administration, should be administered cautiously in patients with or predisposed to seizures or other neurologic disturbances.  The normally recommended dosages should not be exceeded in such patients.  In those with a known seizure disorder, anticonvulsant therapy should be continued during carbapenem therapy.', '2', '', 'DDInter', 0);
