INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (5968, 8042, 'Mesalazine', 'Kidney Diseases', 'The use of mesalamine and other compounds which contain or are converted to mesalamine has rarely been associated with renal adverse effects, including minimal change nephropathy, acute and chronic interstitial nephritis, and nephrogenic diabetes insipidus.  Renal lesions such as renal infarct, papillary necrosis, tubular necrosis and interstitial fibrosis have been reported in high-dose animal studies.  Therapy with mesalamine and prodrugs of mesalamine should be administered cautiously in patients with impaired renal function or a history of renal disease.  Renal function should be evaluated prior to initiation of therapy and periodically during therapy.', '2', '', 'DDInter', 0);
