INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (6009, 5097, 'Arformoterol', 'Hypokalemia', 'Adrenergic bronchodilators may cause decreases in serum potassium concentrations, primarily when given by nebulization or intravenous administration.  Although this effect is usually transient and does not require supplementation, clinically significant hypokalemia may occur in some patients, with the potential to induce cardiovascular adverse effects.  The relevance of these observations to oral or oral aerosol/powder for inhalation therapy is unknown.  Therapy with adrenergic bronchodilators should be administered cautiously in patients with or predisposed to hypokalemia.', '2', '', 'DDInter', 0);
