INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (6133, 614, 'Metformin', 'Vitamin B 12 Deficiency', 'Metformin may interfere with vitamin B12 (cyanocobalamin) absorption from the B12-intrinsic factor complex.  A decrease to subnormal levels of previously normal serum B12 levels has been reported in approximately 7% of patients treated with metformin during controlled clinical trials.  Although the decrease is generally well-tolerated and rarely associated with clinical manifestations such as megaloblastic anemia, caution may be warranted when metformin therapy is administered in patients with preexisting B12 deficiency.  Vitamin B12 supplementation as well as annual measurements of hematologic parameters may be appropriate.', '2', '', 'DDInter', 0);
