INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (6201, 11875, 'Methoxsalen', 'Photosensitivity Disorders', 'The use of methoxsalen with ultraviolet radiation is contraindicated in patients with a history of photosensitive disease states.  Methoxsalen is a potent photosensitizer that can induce severe burns and marked hyperpigmentation and aging of skin in susceptible patients or when used improperly.  Diseases associated with photosensitivity include lupus erythematosus, porphyria cutanea tarda, erythropoietic protoporphyria, variegate porphyria, xeroderma pigmentosum, and albinism.', '3', '', 'DDInter', 0);
