INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (6216, 8642, 'Methoxsalen', 'Sunburn', 'Patients presenting with a sunburn should not be treated with methoxsalen (a psoralen) and ultraviolet radiation (UVA), since it may be difficult to evaluate the response of these patients to therapy.  The mild, transient erythema normally seen 24 to 48 hours after PUVA (psoralen and UVA photochemotherapy) indicates a therapeutic interaction occurring between the psoralen and UVA but may be masked by a sunburn.  Patients generally should avoid sunbathing for at least 24 hours prior to methoxsalen administration and UVA exposure.  The usual restrictions concerning sun exposure after treatment and the prescribed dosage should also be followed closely to avoid severe burns and other phototoxic reactions.', '2', '', 'DDInter', 0);
