INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (6332, 5019, 'Methotrexate', 'Stomatitis', 'Methotrexate induces stomatitis within the oral mucosa and gastrointestinal tract.  Therapy with methotrexate should be administered with extreme caution in patients with peptic ulcer disease or ulcerative colitis.  If vomiting, diarrhea or ulcerative stomatitis occur, treatment should be discontinued until recovery to avoid the risk of hemorraghic enteritis or intestinal perforation which could be fatal.', '3', '', 'DDInter', 0);
