INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7154, 2971, 'Methylphenidate', 'Substance-Related Disorders', 'Central nervous system (CNS) stimulants, especially amphetamines, have significant potential for habituation and abuse.  Tolerance, psychological dependence and severe social dysfunction can develop after prolonged use.  Frank psychotic episodes may also occur in association with chronic intoxication.  Therapy with CNS stimulants should be administered cautiously, if at all, in patients with a history of alcohol or substance abuse.  The use of amphetamines is considered by manufacturers to be contraindicated in such patients.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7155, 2972, 'Methylphenidate', 'Substance-Related Disorders', 'Central nervous system (CNS) stimulants, especially amphetamines, have significant potential for habituation and abuse.  Tolerance, psychological dependence and severe social dysfunction can develop after prolonged use.  Frank psychotic episodes may also occur in association with chronic intoxication.  Therapy with CNS stimulants should be administered cautiously, if at all, in patients with a history of alcohol or substance abuse.  The use of amphetamines is considered by manufacturers to be contraindicated in such patients.', '3', '', 'DDInter', 0);
