INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7157, 2971, 'Methylphenidate', 'Tics', 'Central nervous system (CNS) stimulants have been reported to exacerbate Tourette''s syndrome and other motor and phonic tics.  Therapy with CNS stimulants, if necessary, should be administered cautiously in patients with tic disorders or family history of Tourette''s syndrome.  The manufacturers of the CNS stimulants, methylphenidate (racemic) and dexmethylphenidate (the more pharmacologically active d-enantiomer), consider their use to be contraindicated in such patients.', '3', '', 'DDInter', 0);
