INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7163, 2971, 'Methylphenidate', 'Bipolar Disorder', 'Central nervous system (CNS) stimulants may induce a mixed/manic episode in patients with bipolar disorder.  Prior to initiating treatment, screen patients for risk factors for developing a manic episode (e.g., comorbid or history of depressive symptoms or a family history of suicide, bipolar disorder, and depression).  Close monitoring is recommended when using these agents in patients with bipolar disorders.', '2', '', 'DDInter', 0);
