INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7223, 3423, 'Methylprednisolone', 'Diabetes Mellitus', 'Corticosteroids can raise blood glucose level by antagonizing the action and suppressing the secretion of insulin, which results in inhibition of peripheral glucose uptake and increased gluconeogenesis.  Therapy with corticosteroids should be administered cautiously in patients with diabetes mellitus, glucose intolerance, or a predisposition to hyperglycemia.  Patients with diabetes mellitus should be monitored more closely during corticosteroid therapy, and their antidiabetic regimen adjusted accordingly.', '2', '', 'DDInter', 0);
