INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7394, 142, 'Methyldopa', 'Anemia, Hemolytic', 'Hemolytic anemia has occurred rarely during therapy with methyldopa and may be associated with a (+) direct Coombs.  Therapy with methyldopa should be administered cautiously to patients with anemia or a predisposition to hemolytic syndromes.  Clinical monitoring of hematopoietic function, including a baseline hematological profile, is recommended.  If Coombs- positive hemolytic anemia occurs, the cause may be methyldopa and the drug should be discontinued', '2', '', 'DDInter', 0);
