INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7400, 142, 'Methyldopa', 'Leukopenia', 'Rare reports of reversible reduction in white blood cell count, primarily neutrophils, have been noted.  White blood cell counts returned to normal with discontinuation of methyldopa.  Therapy with methyldopa should be administered cautiously in patients with a history of or predisposition to decreased white blood cell or neutrophil counts. Clinical monitoring of hematopoietic function is recommended.', '2', '', 'DDInter', 0);
