INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7484, 516, 'Metoclopramide', 'Depressive Disorder', 'Metoclopramide has been reported to cause mental depression in patients with and without a prior history of depression.  Symptoms have ranged from mild to severe and have included suicidal ideation and suicide.  Therapy with metoclopramide in patients with a history of mental depression, especially suicidal tendencies, should be administered cautiously, and only if benefits are anticipated to outweigh the potential risks.  Some patients who experience depression with metoclopramide have improved following temporary discontinuation of the drug and reinstitution at a lower dosage with gradual titration.', '3', '', 'DDInter', 0);
