INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7518, 516, 'Metoclopramide', 'Neuroleptic Malignant Syndrome', 'Although not a neuroleptic agent, metoclopramide has antidopaminergic effects and may rarely precipitate or aggravate a potentially fatal symptom complex known as Neuroleptic Malignant Syndrome (NMS).  Clinical manifestations of NMS include hyperpyrexia, muscle rigidity, involuntary movements, altered mental status and autonomic instability (irregular pulse or blood pressure, tachycardia, diaphoresis and cardiac arrhythmias).  Metoclopramide therapy should not be initiated in patients with active NMS and should be immediately discontinued if currently being administered in such patients.  In patients with a history of NMS, introduction or reintroduction of metoclopramide or other antidopaminergic therapy should be carefully considered, since NMS may recur.', '3', '', 'DDInter', 0);
