INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7535, 516, 'Metoclopramide', 'Pheochromocytoma', 'The use of metoclopramide is contraindicated in patients with pheochromocytoma.  In a study involving patients with essential hypertension, intravenously administered metoclopramide was shown to induce the release of catecholamines.  Hypertensive crises may occur in patients with pheochromocytoma due to induction of catecholamine release from the tumor.', '3', '', 'DDInter', 0);
