INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7586, 516, 'Metoclopramide', 'Water-Electrolyte Imbalance', 'Metoclopramide produces a transient increase in plasma aldosterone and may cause fluid retention and volume overload in susceptible patients.  Therapy with metoclopramide should be administered cautiously in patients with or at risk for fluid overload (e.g., cirrhosis, congestive heart failure).  If these side effects occur at any time during metoclopramide therapy, the drug should be discontinued.', '2', '', 'DDInter', 0);
