INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7620, 516, 'Metoclopramide', 'Parkinsonian Disorders', 'Metoclopramide has antidopaminergic effects and may cause parkinsonian-like symptoms such as akinesia, bradykinesia, tremors, pill-rolling motion, cogwheel rigidity, and postural abnormalities including stooped posture and shuffling gait.  The onset is usually within the first six months following initiation of therapy but occasionally after longer periods.  Symptoms generally subside within 2 to 3 months after drug discontinuation.  Therapy with metoclopramide should be administered cautiously, if at all, in patients with Parkinson''s disease or parkinsonian symptoms.', '2', '', 'DDInter', 0);
