INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7637, 516, 'Metoclopramide', 'Kidney Diseases', 'Renal function affects the clearance of metoclopramide.  In patients with varying degrees of renal impairment, a reduction in creatinine clearance was correlated with a reduction in plasma clearance, renal clearance, and non-renal clearance, and increase in elimination half-life of metoclopramide.  Therapy with metoclopramide should be administered cautiously in patients with impaired renal function.  A dosage reduction may be appropriate to avoid drug accumulation.', '2', '', 'DDInter', 0);
