INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7705, 17239, 'Metolazone', 'Diabetes Mellitus', 'Thiazide diuretics may cause hyperglycemia and glycosuria in patients with diabetes.  They may also precipitate diabetes in prediabetic patients.  These effects are usually reversible following discontinuation of the drugs.  Therapy with thiazide diuretics should be administered cautiously in patients with diabetes mellitus, glucose intolerance, or a predisposition to hyperglycemia.  Patients with diabetes mellitus should be monitored more closely during thiazide therapy, and their antidiabetic regimen adjusted accordingly.', '2', '', 'DDInter', 0);
