INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7713, 17239, 'Metolazone', 'Hyperparathyroidism', 'Urinary calcium excretion is decreased by thiazide diuretics during chronic administration.  Pathologic changes in the parathyroid gland with hypercalcemia and hypophosphatemia have been reported during prolonged therapy.  However, the common complications of hyperparathyroidism such as renal lithiasis, bone resorption, and peptic ulceration have not been seen.  Clinicians should be cognizant of these effects when prescribing or administering thiazide therapy to patients with hyperparathyroidism.  These drugs should be discontinued before carrying out tests for parathyroid function.', '2', '', 'DDInter', 0);
