INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7717, 17239, 'Metolazone', 'Hyperuricemia', 'Thiazide diuretics decrease the rate of uric acid excretion.  Hyperuricemia occurs frequently but is usually asymptomatic and rarely leads to clinical gout except in patients with a history of gout or chronic renal failure.  Therapy with thiazide diuretics should be administered cautiously in such patients.', '2', '', 'DDInter', 0);
