INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7801, 30008, 'Metronidazole (topical)', 'Alcoholism', 'Metronidazole may be systemically absorbed when applied to the skin or mucosal membranes.  A disulfiram-like reaction has been reported in patients who consume alcohol during treatment with oral metronidazole.  Symptoms include nausea, vomiting, flushing, sweating, headache, abdominal cramps, and hypotension.  Therapy with topical metronidazole should be administered cautiously in patients who might be prone to acute alcohol intake.  Patients should be instructed to avoid alcohol-containing products during therapy and for at least 48 hours after the last dose.', '2', '', 'DDInter', 0);
