INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (7846, 690, 'Metronidazole (topical)', 'Liver Diseases', 'Metronidazole may be systemically absorbed when applied to the skin or mucosal membranes.  Once absorbed, metronidazole is extensively metabolized by the liver to both pharmacologically active and inactive compounds.  The plasma clearance of metronidazole may be decreased and the half-life prolonged in patients with impaired hepatic function.  Therapy with topical metronidazole should be administered cautiously in patients with liver disease.', '2', '', 'DDInter', 0);
