INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8001, 6077, 'Metronidazole', 'Hematologic Diseases', 'The use of nitroimidazoles (e.g., metronidazole, tinidazole) has rarely been associated with hematologic adverse effects such as mild, transient leukopenia, thrombocytopenia, and bone marrow aplasia.  The manufacturers recommend that therapy with nitroimidazoles be administered cautiously in patients with evidence of or a history of blood dyscrasias, and that total and differential leukocyte counts be performed before and after treatment with these drugs, particularly in patients receiving repeated courses of therapy.', '3', '', 'DDInter', 0);
