INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8367, 3846, 'Midazolam', 'Heart Failure', 'In patients with congestive heart failure, there appears to be a two-fold increase in the elimination half-life, a 25% decrease in the plasma clearance, and a 40% increase in the volume of distribution of midazolam.  Therapy with midazolam should be administered cautiously at reduced initial dosages in patients with congestive heart failure, particularly if they are elderly.  The possibility of profound and/or prolonged effect should be considered.', '3', '', 'DDInter', 0);
