INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8374, 3846, 'Midazolam', 'Liver Diseases', 'Midazolam is metabolized by the liver, and the metabolites are excreted in the urine.  Reduced drug clearance and prolonged elimination half-life of parent drug and metabolites have been reported in patients with renal and/or hepatic impairment.  Therapy with midazolam should be administered cautiously at reduced initial dosages in such patients.  The possibility of profound and/or prolonged effect should be considered.', '3', '', 'DDInter', 0);
