INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8485, 8238, 'Mirtazapine', 'Liver Diseases', 'Mirtazapine is extensively metabolized by the liver.  Both the parent drug and metabolites, some of which are pharmacologically active, are excreted by the kidney.  The clearance of mirtazapine has been shown to decrease in patients with moderate to severe renal or hepatic impairment.  Therapy with mirtazapine should be administered cautiously in such patients.  Dosage adjustments may be necessary.', '3', '', 'DDInter', 0);
