INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8867, 8243, 'Mitoxantrone', 'Heart Diseases', 'Mitoxantrone can cause myocardial toxicity leading to congestive heart failure (CHF).  Patients with preexisting heart disease, prior irradiation, previous therapy with related compounds such as daunorubicin, idarubicin or doxorubicin, or of advanced age are at increased risk of cardiotoxicity at a lower cumulative dose.  The incidence of drug-induced congestive heart failure in adults is increased when the total cumulative dose of mitoxantrone exceeds 140 mg/m2.  Close clinical monitoring of cardiac function, such as determination of left ventricular ejection fraction, prior to each course of therapy is recommended.', '3', '', 'DDInter', 0);
