INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8870, 8243, 'Mitoxantrone', 'Liver Diseases', 'Hepatic impairment reduces mitoxantrone clearance.  Patients with severe hepatic dysfunction (bilirubin > 3.4 mg/dL) have an AUC more than three times greater than that of patients with normal hepatic function receiving the same dose.  Therapy with mitoxantrone should be administered cautiously to patients with significantly compromised hepatic function.', '3', '', 'DDInter', 0);
