INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8900, 15465, 'Modafinil', 'Epilepsy', 'Due to general central nervous system stimulation, therapy with CNS stimulant drugs may cause seizures.  These drugs may lower the convulsive threshold in patients with prior history of seizures or EEG abnormalities, and very rarely in patients with no previous history of seizures.  Therapy with CNS stimulants should be used with caution in patients with or predisposed to seizures.  If seizures appear, therapy should be discontinued.', '2', '', 'DDInter', 0);
