INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8918, 22796, 'Moexipril', 'Hyperkalemia', 'In patients with hyperkalemia, especially those associated with impaired renal function or congestive heart failure, ACE inhibitors may further raise serum potassium levels.  Therapy with ACE inhibitors should be administered cautiously in patients with or predisposed to hyperkalemia, and serum potassium levels should be carefully monitored.  Risk factors for the development of hyperkalemia during ACE inhibitor therapy include renal insufficiency, diabetes mellitus, and the concomitant use of potassium-sparing diuretics, potassium supplements, and/or potassium-containing salt substitutes.', '3', '', 'DDInter', 0);
