INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (8980, 1008, 'Montelukast', 'Liver Failure', 'Reports of cholestatic hepatitis, hepatocellular liver-injury, and mixed-pattern liver injury have been reported in patients treated with montelukast sodium that had underlying potential for liver disease, such as alcohol use or other forms of hepatitis.  Patients with mild-to-moderate hepatic insufficiency and clinical evidence of cirrhosis had evidence of decreased metabolism of montelukast.  No dosage adjustment is required in patients with mild-to-moderate hepatic insufficiency.  The pharmacokinetics of montelukast sodium in patients with more severe hepatic impairment or with hepatitis have not been evaluated.  Use caution when prescribing montelukast to these patients.', '2', '', 'DDInter', 0);
