INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9151, 8332, 'Morphine', 'Gallbladder Diseases', 'Narcotic (opioid) analgesic agents increase smooth muscle tone in the biliary tract, which can lead to spasm and elevated biliary tract pressure, especially in the sphincter of Oddi.  Biliary effects appear to be the most pronounced with morphine, although they do not always occur with therapeutic doses.  Therapy with opioids should be administered cautiously in patients with biliary or gallbladder disease.', '2', '', 'DDInter', 0);
