INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9195, 10102, 'Asenapine', 'Seizures', 'Antipsychotic and neuroleptic drugs can lower the seizure threshold and trigger seizures in a dose-dependent manner.  This risk is greatest in patients with a history of seizures or with conditions that lower the seizure threshold.  Therapy with these drugs should be administered cautiously in patients with a history of seizures or other predisposing factors, such as head trauma, CNS abnormalities, and alcoholism.', '2', '', 'DDInter', 0);
