INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9231, 0, 'Muromonab', 'Cardiomyopathies', 'The use of muromonab-CD3 is contraindicated in patients with uncompensated heart failure.  Cardiovascular collapse, cardiac arrest, severe hypotension, shock, left ventricular dysfunction, and arrhythmias have occurred in patients administered muromonab-CD3.  Patients with cardiovascular impairments are at increased risk for more serious cardiorespiratory complications (respiratory arrest, failure, distress, and/or cardiovascular collapse, cardiac arrest, angina, or myocardial infarction) from muromonab-CD3-associated Cytokine Release Syndrome.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9232, 0, 'Muromonab', 'Psychotic Disorders', 'The use of muromonab-CD3 is contraindicated in patients with or predisposed to seizures.  Seizures,  cerebral edema, confusion, auditory/visual hallucinations, mental status changes, psychosis, mania, lethargy, stupor, or coma have occurred in patients administered muromonab-CD3.  Patient with CNS disorders, cerebrovascular disease,  neurological disturbances are at increased risk for neuro/psychiatric events.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9233, 0, 'Muromonab', 'Edema', 'The use of muromonab-CD3 is contraindicated in patients with fluid overload as evidences by chest X-ray or a 3% or more weight gain in the week prior to muromonab-CD3 therapy.  Patients with fluid overload are at increased risk for more serious cardiopulmonary complications (respiratory arrest, failure, distress, and/or cardiovascular collapse, cardiac arrest, angina, or myocardial infarction) from muromonab-CD3-associated Cytokine Release Syndrome.', '3', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9234, 0, 'Muromonab', 'Fever', 'Muromonab-CD3 should not be administered in the presence of a body temperature of 100 degrees F  (38.7 degrees C) or greater.  Spiking fever (to 107 degrees F) associated with the Cytokine Release Syndrome have occurred.  Antipyretics may be administered to reduce body temperature in this setting.', '2', '', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9235, 1760, 'Mycophenolate mofetil', 'Neutropenia', 'Severe neutropenia has occurred in up to 2% of patients administered mycophenolate.  Therapy with mycophenolate should be administered cautiously in patients with or predisposed to neutropenia.  Mycophenolate should be discontinued or the dose reduced in patients who develop neutropenia (absolute neutrophil count <1300/mm3).   Clinical monitoring of hematopoietic function is recommended.  Complete blood counts should be performed weekly during the first month,  two times a month during the second and third months, and monthly for the remainder of the first year of treatment.', '3', '', 'DDInter', 0);
