INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9265, 1760, 'Mycophenolate mofetil', 'Kidney Diseases', 'Mycophenolate undergoes complete presystemic metabolism to its active form, MPA .  Less than 1% of  MPA is excreted in the urine.  In a single-dose study, the area under the curve (AUC) for MPA in patients with severe chronic renal impairment (creatinine clearance <25 mL/min) was approximately 75% greater than those of healthy volunteers (creatinine clearance >80 mL/min).  The AUC for the inactive  metabolite was 3 to 6 times higher.  Multiple dose studies have not been performed.  No data are available for cardiac transplant patients with severe renal impairment and when benefit outweighs risk, therapy with mycophenolate should be administered with extreme caution.', '2', '', 'DDInter', 0);
