INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9867, 244, 'Nalbuphine', 'Liver Diseases', 'Narcotic (opioid) analgesic agents are extensively metabolized by the liver, and several of them (e.g., codeine, hydrocodone, meperidine, methadone, morphine, propoxyphene) have active metabolites that are further converted to inactive substances.  The serum concentrations of these agents and their metabolites may be increased and the half-lives prolonged in patients with impaired hepatic function.  Therapy with opioids should be administered cautiously and initiated at reduced dosages in patients with liver disease.  Subsequent doses should be titrated based on individual response rather than a fixed dosing schedule.', '3', '', 'DDInter', 0);
