INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9901, 23228, 'Nalbuphine', 'Myocardial Infarction', 'Opiate partial agonists may increase systemic and pulmonary arterial pressure and systemic vascular resistance, particularly when given by intravenous administration.  Data are available for pentazocine and butorphanol.  Therapy with opiate partial agonists should be administered cautiously and only if the benefit justifies the risk in patients with acute myocardial infarction (especially if accompanied by hypertension or left ventricular failure) or coronary insufficiency.', '3', '', 'DDInter', 0);
