INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9912, 244, 'Nalbuphine', 'Intracranial Hypertension', 'The hypoventilation associated with administration of opiate partial agonists can induce cerebral hypoxia and vasodilatation with resultant increase in intracranial pressure.  Unless mechanical ventilation is provided, extreme caution is advised when opiate partial agonists are given to patients head injury, intracranial lesions, or a preexisting elevated CSF pressure.  Also, clinicians treating such patients should be aware that opiate partial agonists may interfere with the evaluation of CNS function, especially with respect to consciousness levels, respiratory status, and pupillary changes.', '3', '', 'DDInter', 0);
