INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (9948, 244, 'Nalbuphine', 'Hypothyroidism', 'Patients with hypothyroidism may have increased risk of respiratory depression and prolonged CNS depression associated with the use of narcotic (opioid) analgesic agents.  These agents may also exacerbate the effects of hypothyroidism such as lethargy, impaired mentation, depression, and constipation.  Therapy with opioids should be administered cautiously and initiated at reduced dosages in patients with uncontrolled hypothyroidism or myxedema.  Subsequent doses should be titrated based on individual response rather than a fixed dosing schedule.', '2', '', 'DDInter', 0);
