INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10018, 4596, 'Nabumetone', 'Liver Diseases', 'The biotransformation of nabumetone to its active metabolite (6-methoxy-2-naphthylacetic acid, or 6MNA) and the further metabolism of 6MNA to inactive substances are dependent upon hepatic function and could be reduced in patients with severe hepatic impairment.  The protein binding of 6MNA may also be altered in liver disease.  Clinical data concerning the potential consequences of these effects are limited.', '2', '', 'DDInter', 0);
