INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10123, 296, 'Naproxen', 'Heart Failure', 'Anaprox and Anaprox DS (brands of naproxen sodium) contain 25 mg and 50 mg of sodium per tablet (approximately 1 mEq/250 mg naproxen), respectively, and Naprosyn suspension contains 39 mg per teaspoonful (approximately 1.5 mEq/125 mg naproxen).  The sodium content should be considered when these products are used in patients with conditions that may require sodium restriction, such as congestive heart failure, hypertension, and fluid retention.', '2', '', 'DDInter', 0);
