INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (10291, 8625, 'Nebivolol', 'Peripheral Vascular Diseases', 'Due to their negative inotropic and chronotropic effects on the heart, beta-adrenergic receptor blocking agents (aka beta-blockers) reduce cardiac output and may precipitate or aggravate symptoms of arterial insufficiency in patients with peripheral vascular disease.  In addition, the nonselective beta-blockers (e.g., propranolol, pindolol, timolol) may attenuate catecholamine-mediated vasodilation during exercise by blocking beta-2 receptors in peripheral vessels.  Therapy with beta-blockers should be administered cautiously in patients with peripheral vascular disease.  Close monitoring for progression of arterial obstruction is advised.', '3', '', 'DDInter', 0);
